sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
  initialize: function () {
  },
  isDatePast: function (strDate) {
    // 現在の日付と渡された日付のGlideDateTimeオブジェクトを作成
    var rightnow = new GlideDateTime();
    var dateTest = new GlideDateTime(strDate);

    if (dateTest.before(rightnow)) {
      // testdateがrightnowより前の場合true
      return true;
    }

    return false;
  },

  isDateToday: function (strDate) {

    var rightnow = new GlideDateTime();
    var dateTest = new GlideDateTime(strDate);
    // rightnowとdateTestの日付部分を取得
    var today = rightnow.getLocalDate();
    var todayTest = dateTest.getLocalDate();

    if (today.compareTo(todayTest) == 0) {
      // 同日
      return true;
    }

    return false;
  },

  type: 'NeedItUtils'
};
